(54, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href="https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/" />Read our developer update</a> for more information.', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '[]', 'pending', 'woocommerce.com', '2024-04-14 07:20:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'airwallex_q1_2024', 'marketing', 'en_US', 'Save big on foreign exchange (FX) fees', 'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.', '[]', 'unactioned', 'woocommerce.com', '2024-04-14 08:21:29', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'wc-refund-returns-page', 'info', 'en_US', 'Configurez une page de Politique en matière de remboursements et de retours pour améliorer la crédibilité de votre boutique.', 'Nous avons créé pour vous un brouillon de page de Politique en matière de remboursements et de retours. Prenez le temps de la consulter et de la mettre à jour en y incluant les informations relatives à votre boutique.', '[]', 'unactioned', 'woocommerce-core', '2024-04-14 07:20:40', NULL, 0, 'plain', '', 0, 1, 'info'),
(62, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connecter à Woo.com', 'Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.', '[]', 'unactioned', 'woocommerce-admin', '2024-04-14 07:43:55', NULL, 0, 'plain', '', 0, 1, 'info'),
